$(document).ready(function(){function withinRange(val,min,max){return(val>=min&&val<max)}
$.fn.dataTable.ext.search.push(function(settings,data,dataIndex){var marketCap=$($('.js-summary-table').DataTable().row(dataIndex).node()).children('.market-cap').data('usd');if(isNaN(marketCap)){marketCap=0;}
var option=$('#filter_marketcap').val();if(option==0){return true;}
else if(option==1){return marketCap>=1e9}
else if(option==2){return withinRange(marketCap,1e8,1e9)}
else if(option==3){return withinRange(marketCap,1e7,1e8)}
else if(option==4){return withinRange(marketCap,1e6,1e7)}
else if(option==5){return withinRange(marketCap,1e5,1e6)}
else if(option==6){return marketCap<1e5}
return false;});$.fn.dataTable.ext.search.push(function(settings,data,dataIndex){var price=$($('.js-summary-table').DataTable().row(dataIndex).node()).children('td:eq(4)').children('a').data('usd');if(isNaN(price)){price=0;}
var option=$('#filter_price').val();if(option==0){return true;}
else if(option==1){return price>=100}
else if(option==2){return withinRange(price,1,100)}
else if(option==3){return withinRange(price,.01,1)}
else if(option==4){return withinRange(price,.0001,.01)}
else if(option==5){return price<.0001}
return false;});$.fn.dataTable.ext.search.push(function(settings,data,dataIndex){var volume=$($('.js-summary-table').DataTable().row(dataIndex).node()).children('td:eq(6)').children('a').data('usd');if(isNaN(volume)){volume=0;}
var option=$('#filter_volume').val();if(option==0){return true;}
else if(option==1){return volume>=1e7}
else if(option==2){return volume>=1e6}
else if(option==3){return volume>=1e5}
else if(option==4){return volume>=1e4}
else if(option==5){return volume>=1e3}
return false;});var table=$('.js-summary-table').DataTable();$('#filter_marketcap, #filter_price, #filter_volume').change(function(){table.draw();rerenderCurrency();});});